import React, { useState, useEffect } from 'react';
import Formulario from './components/Formingreso';
import Form from './components/Form';
import Result from './components/Resultado';
import Header from './components/Header';
import Resume from './components/Resume'; 
import Historial from './components/Historial';
import getFinalBudget from './Helper';
import datos from './datos.json';



const App = () => {
  const [budgetObj, setBudgetObj] = useState(null);

  useEffect(() => {
    // Inicializar el formulario al cargar la página
    // Puedes establecer valores predeterminados o realizar otras acciones aquí.
    // Ejemplo:
    setNombre('');
    setEdad('');
    setTienePropiedades(false);
    setPrimeraVez(true);
    setLocalidad('');
  }, []);

  return (
    <div>
       {/* formulario */}
      <Formulario />

      {/* Encabezado */}
      <Header title="COTIZADOR DE SEGUROS" />
      
      {/* Formulario */}
      <Form setBudgetObj={setBudgetObj} />

      {/* Resultado */}
      {budgetObj && <Result budgetAmount={budgetObj.budgetAmount} />}

      {/* Resume */}
      {budgetObj && <Resume formData={budgetObj.formData} quote={budgetObj.budgetAmount} />}

       {/* Historial */}
       <Historial title="historial"/>

      
    </div>
  );
};

export default App;
