import propertyData from './datos.json'; 

function getPropertyFactor(category, type) {
  const property = propertyData.find(item => item.categoria === category && item.tipo === type);
  return property ? property.factor : 1.0; // Devuelve 1.0 si no se encuentra la propiedad
}

function brandBudget(budget, category, type) {
  const factor = getPropertyFactor(category, type);
  return budget += (budget * factor);
}

export default function getFinalBudget(budget, category, type) {
  let finalBudget = budget;
  finalBudget = brandBudget(finalBudget, category, type);
  return parseFloat(finalBudget).toFixed(2);
}
