import React, { useState } from 'react';
import getFinalBudget from '../Helper';
import datos from '../datos.json';
import '../css/Form.css';

const Form = ({ setBudgetObj }) => {
  const [formData, setFormData] = useState({
    category: '',
    type: '',
  });

  const [error, setError] = useState(false);

  const { category, type } = formData;

  const fieldHandle = e => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const submitForm = e => {
    e.preventDefault();
    if (category.trim() === '' || type.trim() === '') {
      setError(true);
      return;
    }
    setError(false);

    // Obtener el factor correspondiente a la categoría y tipo seleccionados
    const selectedProperty = datos.find(item => item.categoria === category && item.tipo === type);
    const factor = selectedProperty ? selectedProperty.factor : 1.0;

    // Calcular la cantidad presupuestada utilizando el factor
    const budgetAmount = getFinalBudget(2000, category, type) * factor;

    setBudgetObj({
      formData,
      budgetAmount,
    });
    setFormData({
      category: '',
      type: '',
    });
  };

  return (
    <form onSubmit={submitForm}>
      <div>
        <label>Categoría:</label>
        <select
          name="category"
          value={category}
          onChange={fieldHandle}
        >
          <option value="">-- Seleccione --</option>
          <option value="propiedad">Propiedad</option>
          <option value="ubicacion">Ubicación</option>
        </select>
      </div>

      <div>
        <label>Tipo:</label>
        <select
          name="type"
          value={type}
          onChange={fieldHandle}
        >
          <option value="">-- Seleccione --</option>
          <option value="Casa">Casa</option>
          <option value="P.H.">P.H.</option>
          <option value="Depto. Edificio">Depto. Edificio</option>
          <option value="Barrio Privado">Barrio Privado</option>
          <option value="Oficina">Oficina</option>
          <option value="Local Comercial">Local Comercial</option>
          <option value="Depósito Logística">Depósito Logística</option>
          <option value="CABA">CABA</option>
          <option value="Tandil">Tandil</option>
          <option value="Costa Atlántica">Costa Atlántica</option>
          <option value="Patagonia">Patagonia</option>
        </select>
      </div>

      <button type="submit">Cotizar</button>

      {error ? <div>Error: Completar los campos del formulario.</div> : null}
    </form>
  );
};

export default Form;
