import React, { useState } from "react";
import styled from "styled-components";

const AmountWrap = styled.div`
  width: 200px;
  color: #ffffff;
  background-color: #34bfb2;
  text-align: center;
  box-sizing: border-box;
  display: inline-block;
`;

const ResetButton = styled.button`
  background-color: #00838f;
  height: 100px;
  width: 200px;
  text-align: center;
  color: #ffffff;
  text-transform: uppercase;
  font-weight: bold;
  border: none;
  transition: background-color 0.3s ease;
  box-sizing: border-box;
  display: inline-block;
  &:hover {
    background-color: #26c6da;
    cursor: pointer;
  }
  &:focus {
    outline: none;
  }
`;

const Result = ({ budgetAmount }) => {
  const [resetAmount, setResetAmount] = useState(false);

  const handleReset = () => {
    setResetAmount(true);
  };

  // Cuando resetAmount cambia, actualiza el estado a cero
  if (resetAmount) {
    setResetAmount(false);
    // Asumiendo que setBudgetAmount es una función para actualizar el estado en el componente padre
    // Debes pasarla como prop desde el componente padre
    setBudgetAmount(0);
  }

  return (
    <div>
      <AmountWrap>Monto de la Cotización: ${budgetAmount}</AmountWrap>
      <ResetButton onClick={handleReset}>Resetear costos</ResetButton>
    </div>
  );
};

export default Result;
