import React, { useState } from 'react';
import getFinalBudget from '../Helper';
import datos from '../datos.json'; // Ajusta la ruta según tu estructura de carpetas

const Form = ({ setBudgetObj }) => {
  const [error, setError] = useState(false);

  const submitForm = (e) => {
    e.preventDefault();
    setError(false);

    const budgetAmount = getFinalBudget(2000, datos);

    setBudgetObj({
      formData: null, // No hay formData en este punto
      budgetAmount
    });
  };

  return (
    <form onSubmit={submitForm}>
      <button type="submit">Cotizar</button>

      {error ? <div>Error: Completar los campos del formulario.</div> : null}
    </form>
  );
};

export default Form;
