export default function getFinalBudget(budget, data) {
  // Lógica para ajustar el presupuesto según la marca (por ejemplo)
  const brandData = data.find(entry => entry.categoria === 'marca');
  const brandFactor = brandData ? brandData.factor : 1;
  const finalBudget = budget * brandFactor;

  // Puedes agregar más lógica aquí si es necesario

  return parseFloat(finalBudget).toFixed(2);
}
