import React, { useEffect } from 'react';
import styled from 'styled-components';

const ResumeWrapper = styled.div`
  background-color: #f2f2f2;
  border: 2px solid #ccc;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  padding: 20px;
  margin: auto;
  text-align: center;
`;

const Resume = ({ formData, quote }) => {
  const { category, type, squareMeters } = formData;

  useEffect(() => {
    const shouldSave =
      category && type && squareMeters && category.trim() !== '' && type.trim() !== '';

    if (shouldSave) {
      const cotizacion = {
        fechaCotizacion: new Date().toLocaleString(),
        propiedad: category,
        ubicacion: type,
        metrosCuadrados: squareMeters,
        poliza: quote,
      };

      const historialCotizaciones =
        JSON.parse(localStorage.getItem('historialCotizaciones')) || [];

      const isDuplicate = historialCotizaciones.some(
        (item) =>
          item.fechaCotizacion === cotizacion.fechaCotizacion &&
          item.propiedad === cotizacion.propiedad &&
          item.ubicacion === cotizacion.ubicacion &&
          item.metrosCuadrados === cotizacion.metrosCuadrados &&
          item.poliza === cotizacion.poliza
      );

      if (!isDuplicate) {
        const updatedHistorial = [...historialCotizaciones, cotizacion];
        localStorage.setItem('historialCotizaciones', JSON.stringify(updatedHistorial));
      }
    }
  }, [category, type, squareMeters, quote]);

  if (!category || !type || !squareMeters || category.trim() === '' || type.trim() === '') {
    return null;
  }

  return (
    <ResumeWrapper>
      <h2>Resumen cotización:</h2>
      <p>Categoría: {category}</p>
      <p>Tipo: {type}</p>
      <p>Metros Cuadrados: {squareMeters}</p>
      <p>Cotización: ${quote}</p>
    </ResumeWrapper>
  );
};

export default Resume;
